import { createSlice, configureStore } from "@reduxjs/toolkit";
import TasksList from "../component/Tasks/TasksList";

const initialState = { todos: [{ id: 0, task: "", completed: false }] };
function nextTodoId(todos) {
  const maxId = todos.reduce((maxId, todo) => Math.max(todo.id, maxId), -1);
  return maxId + 1;
}
const taskSlice = createSlice({
  name: "tasks",
  initialState: initialState,
  reducers: {
    addTask: (state, action) => {
      return {
        ...state,
        todos: [
          ...state.todos,
          {
            id: nextTodoId(state.todos),
            task: action.payload,
            completed: false,
          },
        ],
      };
    },
    AppendTask(state, action) {
      return {
        ...state,
        todos: [...state.todos, action.payload],
      };
    },
    deleteTask(state, action) {
      return {
        ...state,
        todos: state.todos.filter((todo) => todo.id !== action.payload),
      };
    },
    CompletedTask(state, action) {
      const index = state.todos.findIndex((todo) => todo.id !== action.payload);
      const newArray = [...state.todos];
      newArray[index].completed = true;
      return {
        ...state,
        todos: newArray,
        completed: true,
      };
    },
  },
});

const store = configureStore({
  reducer: { tasks: taskSlice.reducer },
});
export const tasksActions = taskSlice.actions;
export default store;

