import React from "react";
import { connect } from "react-redux";
import { buyCake } from "../Redux";

const CakeContainer = (props) => {
  return (
    <div>
      <h2>Number of Cake - {props.numOfCakes}</h2>
      <button onClick={props.buyCake}>Buy Cake</button>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    numOfCakes: state.cake.numOfCakes,
  };
};
//this function takes the state as a parameter and returns an object

const mapDispatchToProps = (dispatch) => {
  return {
    buyCake: () => dispatch(buyCake()),
    //object = dispatch action creator
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CakeContainer);
//connect HOC connects these functions to the container
//connect sends these function as props to the container
//which means container now receives numOfCakes as props as well as buyCake
//similarly mapDispatchToProps function map the dispatch of our action creator
//as a prop this cakeContainer
