import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { tasksActions } from "../../Store";
import "./TasksList.css";
const TasksList = () => {
  const [inputData, setInputData] = useState("");
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.tasks.todos);
  const completed = useSelector(
    (state) => state.tasksActions.CompletedTask.completed
  );

  const submitHandler = (e) => {
    e.preventDefault();
  };
  const ClickHandler = () => {
    console.log(completed);
    if (inputData.length > 0) {
      dispatch(tasksActions.addTask(inputData));
    }
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <input type="text" onChange={(e) => setInputData(e.target.value)} />

        {todos.map((todo) =>
          todo.id === 0 ? null : (
            <li key={todo.id} className={completed ? "completed-list" : "list"}>
              {todo.task}
              <button
                onClick={() => {
                  dispatch(tasksActions.deleteTask(todo.id));
                }}
              >
                x
              </button>
              <button
                onClick={() => {
                  dispatch(tasksActions.CompletedTask(todo.id));
                }}
              ></button>
            </li>
          )
        )}

        <button onClick={ClickHandler}>submit</button>
      </form>
    </div>
  );
};

export default TasksList;
