import React, { useState } from "react";

import { items } from "./Expenseitemsdata";
import "./App.css";
import NewExpense from "./Components/NewExpense/NewExpense";
import ExpensesFilter from "./Components/ExpensesFilter";
import ExpensesList from "./Components/ExpensesList";

const App = () => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const [expenses, setExpenses] = useState(items);

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
  const filteredExpenses = expenses.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  return (
    <div className="App">
      <div className="Container">
        <NewExpense onAddExpense={addExpenseHandler} />
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        <ExpensesList items={filteredExpenses} />
      </div>
    </div>
  );
};

export default App;

/*we can also use in the conditional statement instead of expensesContent variable
this syntax:
{filteredExpenses.length === 0 && <p>No expenses found.</p>}
{filteredExpenses.length > 0 && filteredExpenses.map((expenses)=>(
  <ExpenseItem key={expense.id} title={expense.title} amount = {expense.amount}
  date={expense.date}/>
))} */
