import React, { useState } from "react";
import { connect } from "react-redux";
import { buyIceCream } from "../Redux";

const IceCreamContainer = (props) => {
  const [counter, setCounter] = useState(1);
  const countHandler = (counter) => {
    const increment = counter + 1;
    const prevCounter = { ...counter };
    return setCounter(increment);
  };
  return (
    <div>
      <h2>Number of IceCreams - {props.numOfIceCream}</h2>
      <h3>hola - {counter}</h3>
      <button onClick={countHandler}>increment</button>
      <button onClick={props.buyIceCream}>Buy IceCream</button>
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    numOfIceCream: state.iceCream.numOfIceCreams,
  };
};
//this function takes the state as a parameter and returns an object

const mapDispatchToProps = (dispatch) => {
  return {
    buyIceCream: () => dispatch(buyIceCream()),
    //object = dispatch action creator
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(IceCreamContainer);
//connect HOC connects these functions to the container
//connect sends these function as props to the container
//which means container now receives numOfCakes as props as well as buyCake
//similarly mapDispatchToProps function map the dispatch of our action creator
//as a prop this cakeContainer
