import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { tasksActions } from "../../Store";
import "./TasksList.css";

const TasksList = () => {
  const [inputData, setInputData] = useState("");
  const [editing, setEditing] = useState(false);
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.tasks.todos);
  let completed = useSelector((state) => state.tasks.todos.completed);
  console.log(completed);
  const submitHandler = (e) => {
    e.preventDefault();
  };
  const ClickHandler = () => {
    console.log(completed);
    if (inputData.length > 0) {
      dispatch(tasksActions.addTask(inputData));
    }
  };

  const handleEditing = () => {
    setEditing(!editing);
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <input
          type="text"
          onChange={(e) => setInputData(e.target.value)}
          className="input-field"
        />

        {todos.map((todo) =>
          todo.id === 0 ? null : (
            <div
              className={todo.completed ? "completed-item-List" : "item-List"}
            >
              {editing && (
                <div>
                  <input
                    key={todo.id}
                    type="text"
                    className="edit"
                    onChange={(e) => {
                      console.log(e.target.value, todo.id);
                    }}
                  />
                </div>
              )}
              <li
                onClick={() => {
                  dispatch(tasksActions.CompletedTask(todo.id));
                }}
                key={todo.id}
                className={todo.completed ? "completed-list" : "list"}
              >
                {todo.task}
                <div className="item-control">
                  <button
                    onClick={() => {
                      dispatch(tasksActions.deleteTask(todo.id));
                    }}
                    className={todo.completed ? "button-completed" : "button"}
                  >
                    x
                  </button>

                  {!todo.completed && (
                    <button onClick={handleEditing} className="button">
                      Edit
                    </button>
                  )}
                </div>
              </li>
            </div>
          )
        )}
        <br />
        <button onClick={ClickHandler} className="button">
          submit
        </button>
      </form>
    </div>
  );
};

export default TasksList;
